Scriptal.EventFunctionTemplate:
local tempGameObjectIdVal = |[1|STRING,NUMBER]|
if type(tempGameObjectIdVal) == "string" then
  tempGameObjectIdVal = GetGameObjectId(tempGameObjectIdVal)
end
local tempGameObjectCommand = {
  id = "SwitchRoute", route = |[2|STRING]|
}
if Tpp.IsEnemyHelicopter(tempGameObjectIdVal) then
  tempGameObjectCommand = {
    id = "RequestRoute", route = |[2|STRING]|
  }
elseif Tpp.IsUAV(tempGameObjectIdVal) then
  tempGameObjectCommand = {
    id = "SetPatrolRoute", route = |[2|STRING]|
  }
elseif Tpp.IsHostage(tempGameObjectIdVal) then
  tempGameObjectCommand = {
    id = "SetSneakRoute", route = |[2|STRING]|
  }
  GameObject.SendCommand(tempGameObjectIdVal, { id = "SetForceScared", scared = false })
  GameObject.SendCommand(tempGameObjectIdVal, { id = "SetEverDown", enabled = false })
  GameObject.SendCommand(tempGameObjectIdVal, { id = "SetHostage2Flag", flag = "unlocked", on = false })
end

GameObject.SendCommand(tempGameObjectIdVal, tempGameObjectCommand)
